import Navbar from "./Navbar";
import Footer from "./Footer";
import React, { useState } from "react";

export default function Resume({ viewer, setViewer, isExpanded, setIsExpanded }) {
    return (
        <div style={containerStyles}>
            <Navbar viewer={viewer} setViewer={setViewer} />

            <div style={contentStyles}>
                <h1>My Resume</h1>

                <div style={sectionStyles}>
                    <h2>Download or View My Resume</h2>
                    <p>Below is my resume. You can view it directly here, or download it if needed.</p>

                    {/* Embed the PDF using iframe */}
                    <iframe
                        src="/path/to/your/resume.pdf"  // Update this with the actual path to your PDF
                        width="100%"
                        height="800px"
                        style={{ border: "none" }}
                    ></iframe>
                </div>

                <div style={sectionStyles}>
                    <h2>Download My Resume</h2>
                    <a href="/path/to/your/resume.pdf" download>
                        <button style={buttonStyles}>Download Resume</button>
                    </a>
                </div>
            </div>

            <Footer />
        </div>
    );
}

// Styling
const containerStyles = {
    fontFamily: "Arial, sans-serif",
    margin: "0 auto",
    padding: "20px",
    maxWidth: "1200px"
};

const contentStyles = {
    padding: "20px",
    borderRadius: "8px",
    backgroundColor: "#f4f4f9",
};

const sectionStyles = {
    marginBottom: "20px",
};

const buttonStyles = {
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
};
