import Navbar from "./Navbar";
import Footer from "./Footer";
import React, { useState } from "react";

export default function Resume({ viewer, setViewer, isExpanded, setIsExpanded }) {
    return (
        <div style={containerStyles}>
            <Navbar viewer={viewer} setViewer={setViewer} />

            <div style={contentContainerStyles}>
                <h1>My Resume</h1>

                <div style={sectionStyles}>
                    <h2>Download or View My Resume</h2>
                    <p>Below is my resume. You can view it directly here, or download it if needed.</p>

                    {/* Embed the PDF using iframe */}
                    <div style={iframeContainerStyles}>
                        <iframe
                            src="/ChrisMoseleyFall2024Resume.pdf"
                            width="100%"
                            height="1000px" // Set a larger height for better visibility
                            style={{ border: "none" }}
                        ></iframe>
                    </div>
                </div>

                <div style={sectionStyles}>
                    <h2>Download My Resume</h2>
                    <a href="/ChrisMoseleyFall2024Resume.pdf" download>
                        <button style={buttonStyles}>Download Resume</button>
                    </a>
                </div>
            </div>

            <Footer />
        </div>
    );
}


// Styling
const containerStyles = {
    fontFamily: "Arial, sans-serif",
    margin: "0",
    padding: "0",
    minHeight: "100vh", // Ensure full page height
    display: "flex",
    flexDirection: "column",
};

const contentContainerStyles = {
    padding: "20px",
    margin: "0 auto",
    maxWidth: "1200px", // Limit the width of the content
    flex: 1, // This will make sure the content takes up the remaining space between header and footer
};

const iframeContainerStyles = {
    width: "100%",
    display: "flex",
    justifyContent: "center", // Center the iframe horizontally
};

const sectionStyles = {
    marginBottom: "20px",
};

const buttonStyles = {
    padding: "10px 20px",
    backgroundColor: "#007BFF",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
};
