import React from "react";
import Navbar from "./Navbar";
import Footer from "./Footer";
// import { Link } from "react-router-dom"; // Assuming you're using react-router for navigation

export default function Homepage({ viewer, setViewer, isExpanded, setIsExpanded }) {
    // Inline styling for the page
    const styles = {
        homepageContainer: {
            fontFamily: "Arial, sans-serif",
            lineHeight: "1.6",
            color: "#333",
        },
        heroSection: {
            backgroundColor: "#f0f4f8",
            padding: "50px 20px",
            textAlign: "center",
        },
        heroContent: {
            maxWidth: "800px",
            margin: "0 auto",
        },
        heroHeading: {
            fontSize: "3rem",
            marginBottom: "10px",
        },
        heroParagraph: {
            fontSize: "1.25rem",
        },
        introSection: {
            padding: "40px 20px",
            textAlign: "center",
        },
        introHeading: {
            fontSize: "2rem",
            marginBottom: "20px",
        },
        introParagraph: {
            fontSize: "1.125rem",
            marginBottom: "30px",
        },
        links: {
            display: "flex",
            justifyContent: "center",
            gap: "20px",
        },
        linkButton: {
            backgroundColor: "#007bff",
            color: "white",
            padding: "10px 20px",
            textDecoration: "none",
            borderRadius: "5px",
            fontSize: "1rem",
            transition: "background-color 0.3s",
        },
        linkButtonHover: {
            backgroundColor: "#0056b3",
        },
    };

    return (
        <div style={styles.homepageContainer}>
            <Navbar viewer={viewer} setViewer={setViewer} />

            <section style={styles.heroSection}>
                <div style={styles.heroContent}>
                    <h1 style={styles.heroHeading}>Hi, I'm [Your Name]</h1>
                    <p style={styles.heroParagraph}>Junior Software Engineer at Iowa State University</p>
                    <p style={styles.heroParagraph}>I create web applications and love to solve problems with code. Welcome to my portfolio!</p>
                </div>
            </section>

            <section style={styles.introSection}>
                <h2 style={styles.introHeading}>Welcome to My Portfolio</h2>
                <p style={styles.introParagraph}>
                    I'm a passionate software engineer currently studying at Iowa State University. On this website, you can
                    explore my skills, check out some of the projects I've worked on, download my resume, or get in touch with me!
                </p>

                <div style={styles.links}>
                    <Link to="/projects" style={styles.linkButton} onMouseEnter={(e) => (e.target.style.backgroundColor = styles.linkButtonHover.backgroundColor)} onMouseLeave={(e) => (e.target.style.backgroundColor = "#007bff")}>
                        View My Projects
                    </Link>
                    <Link to="/skills" style={styles.linkButton} onMouseEnter={(e) => (e.target.style.backgroundColor = styles.linkButtonHover.backgroundColor)} onMouseLeave={(e) => (e.target.style.backgroundColor = "#007bff")}>
                        My Skills
                    </Link>
                    <Link to="/resume" style={styles.linkButton} onMouseEnter={(e) => (e.target.style.backgroundColor = styles.linkButtonHover.backgroundColor)} onMouseLeave={(e) => (e.target.style.backgroundColor = "#007bff")}>
                        Download My Resume
                    </Link>
                    <Link to="/contact" style={styles.linkButton} onMouseEnter={(e) => (e.target.style.backgroundColor = styles.linkButtonHover.backgroundColor)} onMouseLeave={(e) => (e.target.style.backgroundColor = "#007bff")}>
                        Contact Me
                    </Link>
                </div>
            </section>

            <Footer />
        </div>
    );
}
